def get_graph(self):
    """ID: 2b16aa17-f128-4479-96e9-555acdadb389"""
    graph = {"internalCalls": [], "externalCalls": []}

    internal, external = self.create_namespaces_map()

    for src, dst in self.edges:
        uris = []
        for node in [src, dst]:
            if node in internal:
                mod = internal[node]
                uri = self.to_uri(mod, node)
                uris.append(self.namespace_map.get(uri, uri))
            elif node in external:
                mod = external[node]
                uris.append(self.to_external_uri(mod, node))

        if len(uris) == 2:
            # internal uris have been converted to ints
            if type(uris[1]) == str and uris[1].startswith("//"):
                graph["externalCalls"].append([uris[0], uris[1]])
            else:
                graph["internalCalls"].append([uris[0], uris[1]])
    return graph
