class ClassManager:
    """ID: 7f24283f-d574-46c0-afa7-af86732e6314"""
    def __init__(self):
        """ID: 4849a3b1-080b-412e-bbf8-6b7d0e6ed3fb"""
        self.names = {}

    def get(self, name):
        """ID: 47bc7dd6-f7d9-4695-b016-c928e9515b31"""
        if name in self.names:
            return self.names[name]

    def create(self, name, module):
        """ID: 1bd359f5-8911-40fb-937d-e422cda26e75"""
        if not name in self.names:
            cls = ClassNode(name, module)
            self.names[name] = cls
        return self.names[name]

    def get_classes(self):
        """ID: b9d58288-bb16-482c-987b-26a3adb41d14"""
        return self.names

class ClassNode:
    """ID: c1a9584d-2ede-4164-b173-197b10a3c5fe"""
    def __init__(self, ns, module):
        """ID: 87c0135a-044f-431f-b4d2-aa905587b15b"""
        self.ns = ns
        self.module = module
        self.mro = [ns]
    def add_parent(self, parent):
        """ID: 71e4ac39-ae48-4246-b443-9b4c3adc42c3"""
        if isinstance(parent, str):
            self.mro.append(parent)
        elif isinstance(parent, list):
            for item in parent:
                if item in self.mro:
                    continue
                self.mro.append(item)
        self.fix_mro()

    def fix_mro(self):
        """ID: 1ce2fb9a-3993-440f-8d03-23e731705131"""
        new_mro = []
        for idx, item in enumerate(self.mro):
            if self.mro[idx+1:].count(item) > 0:
                continue
            new_mro.append(item)
        self.mro = new_mro

    def get_mro(self):
        """ID: bdacb5f4-0cd4-463f-bd42-012f05d376c1"""
        return self.mro

    def get_module(self):
        """ID: f0be8e4b-7681-4558-9015-9987dc51170b"""
        return self.module

    def compute_mro(self):
        """ID: 330bba9e-46bd-43f0-a76a-52f5276d1faa"""
        res = []
        self.mro.reverse()
        for parent in self.mro:
            if not parent in res:
                res.append(parent)

        res.reverse()
        self.mro = res

    def clear_mro(self):
        """ID: f9d71b8b-b9dd-4b73-be4b-c0f9b3606521"""
        self.mro = [self.ns]
