def _get_last_line(self, node):
    """ID: 75603f78-aa89-4102-a3e6-f35198f88d0d"""
    lines = sorted(
        list(ast.walk(node)),
        key=lambda x: x.lineno if hasattr(x, "lineno") else 0,
        reverse=True,
    )
    if not lines:
        return node.lineno
    last = getattr(lines[0], "lineno", node.lineno)
    if last < node.lineno:
        return node.lineno
    return last
