import utils


class ReturnManager(object):
    """ID: 5176d299-37a3-417d-94b6-08d671080f12"""

    def __init__(self):
        """ID: 6800e6e6-ac3d-4307-9948-c8b25a967731"""

        self.returns = {}

    def get_return(self):
        """ID: 427d3e3c-09b5-48e9-8d75-fab84f085bc5"""
        print("\n\n")
        print(f"-> self.returns", self.returns)
        print("\n")
        return self.returns

    def add_returnitem(self, currentNs, left, row, right: str):
        """ID: 4f5e1a20-0899-4b50-a731-4f0984713624"""
        right = utils.join_ns(*right.split(".")[:-1])

        print("add_returnitem \n\n")
        item = ReturnItem(left, row, right)
        if not currentNs in self.returns:
            self.returns[currentNs] = []
        self.returns[currentNs].append(item)

    def get_returnitem(self, currentNs):
        """ID: 706155fd-4c21-4ccb-aff2-4f5dab67b5b0"""
        print("get_returnitem \n\n")
        if currentNs in self.returns:
            return self.returns[currentNs]

    def pop_returnItem(self, currentNs, row, funcName):
        """ID: f6b83e0f-d184-4f22-bd1b-34cf95e3dc0b"""
        if not currentNs in self.returns:
            return None
        for item in self.returns[currentNs]:
            if item.row == row and item.right == funcName:
                self.returns[currentNs].remove(item)
                return item.left

    def peek_returnItem(self, currentNs, row, funcName):
        """ID: 799722dc-232a-49fd-81a8-658ebab13426"""
        print("peek_returnItem")
        if not currentNs in self.returns:
            return None
        for item in self.returns[currentNs]:
            if item.row == row and item.right == funcName:
                return item.left


class ReturnItem:
    """ID: d94a7ff3-7105-409b-bc5f-012fe20348f4"""

    def __init__(self, left, row, right):
        """ID: 578e1c1f-c60d-48e4-89d7-2da9bcd0bbb2"""
        self.left = left
        self.row = row
        self.right = right
