def add_lit_arg(self, name, item):
    """ID: cdbf77a3-7b7f-45fc-aa10-b59e5c30710e"""
    arg = self.get_or_create(name)
    if isinstance(item, str):
        arg.add(LiteralPointer.STR_LIT)
